/**
 * Product type definition
 *
 * This represents the core data model for our product catalog.
 * All fields are required when fetching from the API.
 */
export interface Product {
  id: string
  name: string
  description: string
  price: number
  category: string
  stock: number
  sku: string
  createdAt: string
  updatedAt: string
}

/**
 * Input type for creating a new product
 *
 * The id, createdAt, and updatedAt fields are generated by the server,
 * so they're not included in the creation payload.
 */
export type CreateProductInput = Omit<Product, 'id' | 'createdAt' | 'updatedAt'>

/**
 * Input type for updating an existing product
 *
 * All fields are optional except for the id which is passed separately.
 */
export type UpdateProductInput = Partial<CreateProductInput>

/**
 * Available product categories
 *
 * Used for filtering and form validation.
 */
export const PRODUCT_CATEGORIES = [
  'Electronics',
  'Furniture',
  'Lighting',
  'Accessories',
] as const

export type ProductCategory = (typeof PRODUCT_CATEGORIES)[number]
